import "./EndingScreen.css";
import { useState } from "react";

export default function EndingScreen({ onBack }) {
  const [page, setPage] = useState("ending");

  return (
    <div className="container">
      {page === "ending" && (
        <>
          <h1 className="gameEndingText">Game over</h1>
          <button className="newGameBtn" onClick={() => setPage("newGame")}>New Game</button>
          <button className="backToStartingScreen" onClick={() => onBack("menu")}>Back to starting screen</button>
        </>
      )}

      {page === "newGame" && (
        <>
          <h1 className="gameEndingText">Starting New Game...</h1>
          <button className="backToEndingScreen" onClick={() => setPage("ending")}>Back</button>
        </>
      )}
    </div>
  );
}
